INSERT INTO documentTemplates VALUES (1,NULL,NULL,'Р”РћР“РћР’РћР  Р—Рђ РџРћРўР Р•Р‘Р�РўР•Р›РЎРљР� РљР Р•Р”Р�Рў','	\r\n	\r\n	\r\n	<style type=\"text/css\">P { margin-bottom: 0.08in; direction: ltr; color: rgb(0, 0, 0); widows: 2; orphans: 2; }P.western { font-family: \"Times New Roman\",serif; font-size: 12pt; }P.cjk { font-family: \"Times New Roman\",serif; font-size: 12pt; }P.ctl { font-family: \"Times New Roman\",serif; font-size: 12pt; }</style>\r\n\r\n\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" lang=\"bg-BG\"><a name=\"__DdeLink__0_628689697\"></a>\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">		<b>Р”РћР“РћР’РћР  Р—Рђ\r\nРџРћРўР Р•Р‘Р�РўР•Р›РЎРљР� РљР Р•Р”Р�Рў</b></font></font></p>\r\n<p class=\"western\" style=\"margin-left: 0.49in; text-indent: 0.49in; margin-bottom: 0in; line-height: 115%\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-left: 0.49in; text-indent: 0.49in; margin-bottom: 0in; line-height: 115%\" lang=\"bg-BG\">\r\nв„–<font size=\"2\">$id</font><font face=\"Verdana, sans-serif\"><font size=\"2\">/$currentDate</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"text-indent: 0.3in; margin-bottom: 0in; line-height: 115%\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р”РЅРµСЃ </font></font><font face=\"Verdana, sans-serif\"><font size=\"2\"><font face=\"Verdana, sans-serif\"><font size=\"2\">$currentDate </font></font>РІ\r\nРіСЂ. </font></font><font face=\"Verdana, sans-serif\"><font size=\"2\"><font face=\"Verdana, sans-serif\"><font size=\"2\">$currentCity</font></font> , РјРµР¶РґСѓ:</font></font></p>\r\n<ol><li><p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n	вЂћ<font face=\"Verdana, sans-serif\"><font size=\"2\"><b>РљР Р•Р”Р�Р›РђР™РќвЂќРћРћР”</b>,\r\n	Р°РґСЂРµСЃ РЅР° СѓРїСЂР°РІР»РµРЅРёРµ РіСЂ.РЇРјР±РѕР», СѓР».\r\n	вЂќР�РЅРґСѓСЃС‚СЂРёР°Р»РЅР°вЂќ в„–1, Р•Р�Рљ 200843020, СЂРµРіРёСЃС‚СЂРёСЂР°РЅ\r\n	РєР°С‚Рѕ С„РёРЅР°РЅСЃРѕРІР° РёРЅСЃС‚РёС‚СѓС†РёСЏ РїРѕ Р—РљР� СЃ\r\n	СѓРґРѕСЃС‚РѕРІРµСЂРµРЅРёРµ в„– BG<span lang=\"en-US\">R</span>00044,\r\n	РїСЂРµРґСЃС‚Р°РІР»СЏРІР°РЅР° РѕС‚ Р•РјРёР» РќРёС…СЂР°РЅ РџР°РїР°Р·СЏРЅ,\r\n	СЃ Р•Р“Рќ 7706053466 - СѓРїСЂР°РІРёС‚РµР», РЅР°СЂРёС‡Р°РЅРѕ Р·Р°\r\n	РєСЂР°С‚РєРѕСЃС‚ вЂћРљР Р•Р”Р�РўРћР вЂќ, РѕС‚ РµРґРЅР° СЃС‚СЂР°РЅР°\r\n	Рё</font></font></p>\r\n	</li><li><p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n	$name $midname $surname <br><font face=\"Verdana, sans-serif\"><font size=\"2\">Р•Р“Рќ $egn, Р».Рє.в„–$lkno,\r\n	РёР·Рґ. РЅР° $lkissuedate РѕС‚ РњР’Р  вЂ“ РіСЂ. $lkissueplace,\r\n	СЃ РїРѕСЃС‚РѕСЏРЅРµРЅ Р°РґСЂРµСЃ &lt;br/&gt;РіСЂ./СЃ/$lkaddress\r\n	&lt;br/&gt;Рё РЅР°СЃС‚РѕСЏС‰ Р°РґСЂРµСЃ </font></font><font face=\"Verdana, sans-serif\"><font size=\"2\"><font face=\"Verdana, sans-serif\"><font size=\"2\">&lt;br/&gt;</font></font>$currentAddress</font></font><font face=\"Verdana, sans-serif\"><font size=\"2\">&lt;br/&gt;</font></font></p>\r\n</li></ol>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">РјРµСЃС‚РѕСЂР°Р±РѕС‚Р°вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦<span lang=\"en-US\">вЂ¦.</span>.,\r\nС‚РµР»./GSM/<span lang=\"en-US\">вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦</span>вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦.,</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">РѕС‚ РґСЂСѓРіР° СЃС‚СЂР°РЅР°,\r\nРЅ<span lang=\"en-US\">Р°СЂРёС‡Р°РЅ </span>Р·Р° РєСЂР°С‚РєРѕСЃС‚\r\nвЂћРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›вЂќ, СЃРµ СЃРєР»СЋС‡Рё РЅР°СЃС‚РѕСЏС‰РёСЏС‚\r\nРґРѕРіРѕРІРѕСЂ Р·Р° СЃР»РµРґРЅРѕС‚Рѕ:</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b>I. РџР Р•Р”РњР•Рў РќРђ\r\nР”РћР“РћР’РћР Рђ</b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».1. РљР Р•Р”Р�РўРћР РЄРў\r\nРїСЂРµРґРѕСЃС‚Р°РІСЏ РЅР° РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇ, Р·Р°\r\nСЃСЂРѕРє РѕС‚ ...........РјРµСЃРµС†Р°, РїРѕС‚СЂРµР±РёС‚РµР»СЃРєРё\r\nРєСЂРµРґРёС‚ РІ СЂР°Р·РјРµСЂ РЅР° $sum <span lang=\"en-US\">(</span>вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦..<span lang=\"en-US\">)\r\n</span>Р»РµРІР°, РЅР°СЂРµС‡РµРЅ РєСЂРµРґРёС‚.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».2. РљСЂРµРґРёС‚СЉС‚\r\nСЃРµ СѓСЃРІРѕСЏРІР° РѕС‚ РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў\r\nРµРґРЅРѕРєСЂР°С‚РЅРѕ РїСЂРё СЃРєР»СЋС‡РІР°РЅРµС‚Рѕ РЅР° РґРѕРіРѕРІРѕСЂР°.\r\nРЎ РїРѕРґРїРёСЃРІР°РЅРµ РЅР° РЅР°СЃС‚РѕСЏС‰РёСЏ РґРѕРіРѕРІРѕСЂ,\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў СѓРґРѕСЃС‚РѕРІРµСЂСЏРІР°\r\nРїРѕР»СѓС‡Р°РІР°РЅРµС‚Рѕ РЅР° РєСЂРµРґРёС‚Р° РѕС‚ РљР Р•Р”Р�РўРћР Рђ.</font></font></p>\r\n<p class=\"western\" style=\"margin-left: 0.13in; margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b>II. Р›Р�РҐР’Р�,\r\nРўРђРљРЎР� Р� РљРћРњР�РЎР�РћРќРќР�</b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».3. Р—Р°\r\nРїСЂРµРґРѕСЃС‚Р°РІРµРЅРёСЏ РєСЂРµРґРёС‚ РїРѕ С‡Р». 1,\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў Р·Р°РїР»Р°С‰Р° РІСЉР·РЅР°РіСЂР°Р¶РґРµРЅРёРµ\r\nРЅР° РљР Р•Р”Р�РўРћР Рђ РїРѕРґ С„РѕСЂРјР°С‚Р° РЅР° <span lang=\"en-US\">С„РёРєСЃРёСЂР°РЅР°\r\n</span>РјРµСЃРµС‡РЅР° Р»РёС…РІР° РІ СЂР°Р·РјРµСЂ РЅР° ............\r\n(СЃР»РѕРІРѕРј). Р”РЅРµРІРЅРёСЏС‚ СЂР°Р·РјРµСЂ РЅР° Р»РёС…РІР°С‚Р° РїРѕ\r\nРїСЂРµРґС…РѕРґРЅРѕС‚Рѕ РёР·СЂРµС‡РµРЅРёРµ Рµ РІ СЂР°Р·РјРµСЂ РЅР°\r\n..............................(СЃР»РѕРІРѕРј), Р° РіРѕРґРёС€РЅРёСЏС‚ -\r\n......................(СЃР»РѕРІРѕРј).</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».4. РџСЂРё\r\nСЃРєР»СЋС‡РІР°РЅРµ РЅР° РґРѕРіРѕРІРѕСЂР° РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў\r\nР·Р°РїР»Р°С‰Р° С‚Р°РєСЃР° РѕР±СЃР»СѓР¶РІР°РЅРµ РїРѕ РєСЂРµРґРёС‚Р° РІ\r\nСЂР°Р·РјРµСЂ РЅР° ......................(СЃР»РѕРІРѕРј).</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».5. РђРєРѕ\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў Р·Р°Р±Р°РІРё РїР»Р°С‰Р°РЅРµ РЅР°\r\nРІРЅРѕСЃРєР°, С‚РѕР№ РґСЉР»Р¶Рё РѕР±РµР·С‰РµС‚РµРЅРёРµ РІ СЂР°Р·РјРµСЂ\r\nРЅР° Р·Р°РєРѕРЅРЅР°С‚Р° Р»РёС…РІР° Р·Р° РїРµСЂРёРѕРґР° РЅР° Р·Р°Р±Р°РІР°С‚Р°.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».6. <span lang=\"en-US\">(</span>1<span lang=\"en-US\">)</span>\r\nР“РѕРґРёС€РЅРёСЏС‚ РїСЂРѕС†РµРЅС‚ РЅР° СЂР°Р·С…РѕРґРёС‚Рµ РїРѕ\r\nРєСЂРµРґРёС‚Р° Рµ РІ СЂР°Р·РјРµСЂ РЅР° вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦.(СЃР»РѕРІРѕРј).</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><span lang=\"en-US\">(</span>2<span lang=\"en-US\">)</span>\r\nРћР±С‰Р°С‚Р° СЃСѓРјР° РґСЉР»Р¶РёРјР° РѕС‚ РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇ,\r\nРёР·С‡РёСЃР»РµРЅР° РєСЉРј РјРѕРјРµРЅС‚Р° РЅР° СЃРєР»СЋС‡РІР°РЅРµ РЅР°\r\nРЅР°СЃС‚РѕСЏС‰РёСЏ РґРѕРіРѕРІРѕСЂ Рµ вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦вЂ¦(СЃР»РѕРІРѕРј).\r\n </font></font>\r\n</p>\r\n<p class=\"western\" style=\"text-indent: 0.39in; margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b><span lang=\"en-US\">III.\r\nРЈР›РћР’Р�РЇ Р—Рђ </span>РџРћР“РђРЎРЇР’РђРќР•</b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».7. (1)\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў СЃРµ Р·Р°РґСЉР»Р¶Р°РІР° РґР°\r\nРІСЉСЂРЅРµ РЅР° РљР Р•Р”Р�РўРћР Рђ РєСЂРµРґРёС‚Р° Р·Р°РµРґРЅРѕ СЃ\r\nРІСЉР·РЅР°РіСЂР°Р¶РґРµРЅРёРµС‚Рѕ РїРѕ С‡Р». 3 РЅР° ......... (СЃР»РѕРІРѕРј)\r\nСЂР°РІРЅРё РїРѕРіР°СЃРёС‚РµР»РЅРё РІРЅРѕСЃРєРё, СЃСЉСЃС‚РѕСЏС‰Рё СЃРµ\r\nРѕС‚ СЃСЉРѕС‚РІРµС‚РЅР° С‡Р°СЃС‚ РѕС‚ РіР»Р°РІРЅРёС†Р°С‚Р° Рё\r\nР»РёС…РІР°С‚Р°, РІСЃСЏРєР° РІ СЂР°Р·РјРµСЂ РЅР°\r\n...................(СЃР»РѕРІРѕРј) Р»РµРІР° РїСЂРё СѓСЃР»РѕРІРёСЏС‚Р°\r\nРё РІ СЃСЂРѕРєРѕРІРµС‚Рµ, РѕРїСЂРµРґРµР»РµРЅРё РІ РџРѕРіР°СЃРёС‚РµР»РЅРёСЏ\r\nРїР»Р°РЅ вЂ“ РџСЂРёР»РѕР¶РµРЅРёРµ 1, РЅРµСЂР°Р·РґРµР»РЅР° С‡Р°СЃС‚ РѕС‚\r\nРЅР°СЃС‚РѕСЏС‰РёСЏС‚ РґРѕРіРѕРІРѕСЂ. </font></font>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">(2) РџРѕРіР°СЃРёС‚РµР»РЅРёСЏС‚\r\nРїР»Р°РЅ СЃСЉРґСЉСЂР¶Р° С‚РѕС‡РЅР° РёРЅС„РѕСЂРјР°С†РёСЏ Р·Р° СЂР°Р·РјРµСЂР°,\r\nР±СЂРѕСЏ, РїРµСЂРёРѕРґРёС‡РЅРѕСЃС‚С‚Р° Рё РґР°С‚Р°С‚Р° РЅР° РїР»Р°С‰Р°РЅРµ\r\nРЅР° РїРѕРіР°СЃРёС‚РµР»РЅРёС‚Рµ РІРЅРѕСЃРєРё. РљРѕРіР°С‚Рѕ РїР°РґРµР¶СЉС‚\r\nРЅР° РґСЉР»Р¶РёРјР°С‚Р° РІРЅРѕСЃРєР° Рµ РЅРµРїСЂРёСЃСЉСЃС‚РІРµРЅ РґРµРЅ,\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў СЏ Р·Р°РїР»Р°С‰Р° РІ СЃР»РµРґРІР°С‰РёСЏ\r\nСЂР°Р±РѕС‚РµРЅ РґРµРЅ, РєР°С‚Рѕ РІ С‚РѕР·Рё СЃР»СѓС‡Р°Р№ РЅРµ Рµ РІ\r\nР·Р°Р±Р°РІР°.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b>IV. РџР Р•Р”РЎР РћР§РќРћ\r\nРџРћР“РђРЎРЇР’РђРќР• РќРђ РљР Р•Р”Р�РўРђ</b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».8. (1)\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў РёРјР° РїСЂР°РІРѕ РїРѕ РІСЃСЏРєРѕ\r\nРІСЂРµРјРµ РґР° РїРѕРіР°СЃРё РёР·С†СЏР»Рѕ РёР»Рё С‡Р°СЃС‚РёС‡РЅРѕ\r\nР·Р°РґСЉР»Р¶РµРЅРёСЏС‚Р° СЃРё РїРѕ РґРѕРіРѕРІРѕСЂР° Р·Р° РєСЂРµРґРёС‚,\r\nСЃСЉРѕР±СЂР°Р·РЅРѕ СѓСЃР»РѕРІРёСЏС‚Р° РЅР° С‚. 2.13 РѕС‚ РћР±С‰РёС‚Рµ\r\nСѓСЃР»РѕРІРёСЏ. </font></font>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">(2) Р’ СЃР»СѓС‡Р°РёС‚Рµ\r\nРїРѕ Р°Р». 1 РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў РёРјР° РїСЂР°РІРѕ\r\nРґР° РїРѕР»СѓС‡Рё РїСЂРё РїРѕРёСЃРєРІР°РЅРµ Рё Р±РµР·РІСЉР·РјРµР·РґРЅРѕ,\r\nРІСЉРІ РІСЃРµРєРё РјРѕРјРµРЅС‚ РЅР° РёР·РїСЉР»РЅРµРЅРёРµ РЅР°\r\nРґРѕРіРѕРІРѕСЂР°, РёР·РІР»РµС‡РµРЅРёРµ РїРѕ СЃРјРµС‚РєР° РїРѕРґ\r\nС„РѕСЂРјР°С‚Р° РЅР° РїРѕРіР°СЃРёС‚РµР»РµРЅ РїР»Р°РЅ Р·Р° РёР·РІСЉСЂС€РµРЅРёС‚Рµ\r\nРё РїСЂРµРґСЃС‚РѕСЏС‰РёС‚Рµ РїР»Р°С‰Р°РЅРёСЏ; РїРѕРіР°СЃРёС‚РµР»РЅРёСЏС‚\r\nРїР»Р°РЅ РїРѕСЃРѕС‡РІР° РґСЉР»Р¶РёРјРёС‚Рµ РїР»Р°С‰Р°РЅРёСЏ Рё\r\nСЃСЂРѕРєРѕРІРµС‚Рµ Рё СѓСЃР»РѕРІРёСЏС‚Р° Р·Р° РёР·РІСЉСЂС€РІР°РЅРµС‚Рѕ\r\nРЅР° С‚РµР·Рё РїР»Р°С‰Р°РЅРёСЏ; РїР»Р°РЅСЉС‚ СЃСЉРґСЉСЂР¶Р° СЂР°Р·Р±РёРІРєР°\r\nРЅР° РІСЃСЏРєР° РїРѕРіР°СЃРёС‚РµР»РЅР° РІРЅРѕСЃРєР°, РїРѕРєР°Р·РІР°С‰Р°\r\nРїРѕРіР°СЃСЏРІР°РЅРµС‚Рѕ РЅР° РіР»Р°РІРЅРёС†Р°С‚Р° Рё Р»РёС…РІР°С‚Р°,\r\nРёР·С‡РёСЃР»РµРЅР° РЅР° Р±Р°Р·Р°С‚Р° РЅР° Р»РёС…РІРµРЅРёСЏ РїСЂРѕС†РµРЅС‚\r\nРїРѕ С‡Р». 3 РѕС‚ РґРѕРіРѕРІРѕСЂР°.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">(3)</font> <font size=\"2\">РџСЂРё\r\nРїСЂРµРґСЃСЂРѕС‡РЅРѕ РїРѕРіР°СЃСЏРІР°РЅРµ РЅР° РєСЂРµРґРёС‚Р°\r\nРљР Р•Р”Р�РўРћР РЄРў РёРјР° РїСЂР°РІРѕ РЅР° РѕР±РµР·С‰РµС‚РµРЅРёРµ.\r\nРћР±РµР·С‰РµС‚РµРЅРёРµС‚Рѕ РЅР° РљСЂРµРґРёС‚РѕСЂР° РЅРµ РјРѕР¶Рµ РґР°\r\nР±СЉРґРµ РїРѕ-РіРѕР»СЏРјРѕ РѕС‚ 1 % РѕС‚ РїСЂРµРґСЃСЂРѕС‡РЅРѕ\r\nРїРѕРіР°СЃРµРЅР°С‚Р° СЃСѓРјР° РїРѕ РєСЂРµРґРёС‚Р°, РєРѕРіР°С‚Рѕ\r\nРѕСЃС‚Р°РІР°С‰РёСЏС‚ РїРµСЂРёРѕРґ РЅР° РґРѕРіРѕРІРѕСЂР° Р·Р° РєСЂРµРґРёС‚\r\nРµ РїРѕ-РіРѕР»СЏРј РѕС‚ РµРґРЅР° РіРѕРґРёРЅР°. РљРѕРіР°С‚Рѕ\r\nРѕСЃС‚Р°РІР°С‰РёСЏС‚ РїРµСЂРёРѕРґ РЅР° РґРѕРіРѕРІРѕСЂР° Рµ РїРѕ-РјР°Р»СЉРє\r\nРѕС‚ РµРґРЅР° РіРѕРґРёРЅР°, РѕР±РµР·С‰РµС‚РµРЅРёРµС‚Рѕ Рµ 0.5 % РѕС‚\r\nСЃСѓРјР°С‚Р° РЅР° РїСЂРµРґСЃСЂРѕС‡РЅРѕ РїРѕРіР°СЃРµРЅРёСЏ РєСЂРµРґРёС‚.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».9. РљР Р•Р”Р�РўРћР РЄРў\r\nРЅРµ РјРѕР¶Рµ РґР° РѕС‚РєР°Р¶Рµ РґР° РїСЂРёРµРјРµ РїСЂРµРґСЃСЂРѕС‡РЅРѕ\r\nРёР·РїСЉР»РЅРµРЅРёРµ РїРѕ РґРѕРіРѕРІРѕСЂР° Р·Р° РєСЂРµРґРёС‚.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b><span lang=\"en-US\">V.\r\nРћРўРљРђР— РћРў Р”РћР“РћР’РћР Рђ Р—Рђ </span> <span lang=\"en-US\">РљР Р•Р”Р�Рў</span></b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».10. (1)\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў РёРјР° РїСЂР°РІРѕ РґР° СЃРµ\r\nРѕС‚РєР°Р¶Рµ РѕС‚ СЃРєР»СЋС‡РµРЅРёСЏ РґРѕРіРѕРІРѕСЂ Р·Р°\r\nРїРѕС‚СЂРµР±РёС‚РµР»СЃРєРё РєСЂРµРґРёС‚</font>, <font size=\"2\">Р±РµР·\r\nРґР° РґСЉР»Р¶Рё РѕР±РµР·С‰РµС‚РµРЅРёРµ РёР»Рё РЅРµСѓСЃС‚РѕР№РєР° Рё\r\nР±РµР· РґР° РїРѕСЃРѕС‡РІР° РїСЂРёС‡РёРЅР°, РІ СЃСЂРѕРє 14 РґРЅРё,\r\nСЃС‡РёС‚Р°РЅРѕ РѕС‚ РґР°С‚Р°С‚Р° РЅР° РЅРµРіРѕРІРѕС‚Рѕ РїРѕРґРїРёСЃРІР°РЅРµ.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">(2) РљРѕРіР°С‚Рѕ\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў СѓРїСЂР°Р¶РЅРё РїСЂР°РІРѕС‚Рѕ СЃРё\r\nРїРѕ Р°Р». 1, С‚РѕР№ РІСЂСЉС‰Р° РЅР° РљСЂРµРґРёС‚РѕСЂР° РіР»Р°РІРЅРёС†Р°С‚Р°\r\nРё Р·Р°РїР»Р°С‰Р° РґРЅРµРІРЅР°С‚Р° Р»РёС…РІР° РїРѕ С‡Р». 3 РѕС‚\r\nРґРѕРіРѕРІРѕСЂР°, РЅР°С‡РёСЃР»РµРЅР° Р·Р° РїРµСЂРёРѕРґР° РѕС‚ РґР°С‚Р°С‚Р°\r\nРЅР° СѓСЃРІРѕСЏРІР°РЅРµ РЅР° СЃСЂРµРґСЃС‚РІР° РїРѕ РєСЂРµРґРёС‚Р° РґРѕ\r\nРґР°С‚Р°С‚Р° РЅР° РІСЂСЉС‰Р°РЅРµ РЅР° РіР»Р°РІРЅРёС†Р°С‚Р°, Р±РµР·\r\nРЅРµРѕРїСЂР°РІРґР°РЅРѕ Р·Р°Р±Р°РІСЏРЅРµ Рё РЅРµ РїРѕ-РєСЉСЃРЅРѕ РѕС‚\r\n30 (С‚СЂРёРґРµСЃРµС‚) РєР°Р»РµРЅРґР°СЂРЅРё РґРЅРё, СЃС‡РёС‚Р°РЅРѕ РѕС‚\r\nРёР·РїСЂР°С‰Р°РЅРµС‚Рѕ РЅР° СѓРІРµРґРѕРјР»РµРЅРёРµС‚Рѕ РґРѕ РљР Р•Р”Р�РўРћР Рђ\r\nР·Р° СѓРїСЂР°Р¶РЅСЏРІР°РЅРµ РїСЂР°РІРѕС‚Рѕ РЅР° РѕС‚РєР°Р·. </font></font>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">(3) РџСЂРё СѓРїСЂР°Р¶РЅСЏРІР°РЅРµ\r\nРїСЂР°РІРѕС‚Рѕ РЅР° РѕС‚РєР°Р· РѕС‚ СЃРєР»СЋС‡РµРЅРёСЏ РґРѕРіРѕРІРѕСЂ\r\nРљР Р•Р”Р�РўРћР РЄРў РЅСЏРјР° РїСЂР°РІРѕ РґР° РёР·РёСЃРєРІР° Рё\r\nСЃСЉР±РёСЂР° РѕС‚ РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇ РѕР±РµР·С‰РµС‚РµРЅРёРµ,\r\nСЃ РёР·РєР»СЋС‡РµРЅРёРµ РЅР° РѕР±РµР·С‰РµС‚РµРЅРёРµС‚Рѕ Р·Р°\r\nРЅР°РїСЂР°РІРµРЅРёС‚Рµ РѕС‚ РЅРµРіРѕ СЂР°Р·С…РѕРґРё РєСЉРј РїСѓР±Р»РёС‡РЅРё\r\nР°РґРјРёРЅРёСЃС‚СЂР°С‚РёРІРЅРё РѕСЂРіР°РЅРё, РєРѕРёС‚Рѕ РЅРµ РїРѕРґР»РµР¶Р°С‚\r\nРЅР° РІСЉР·СЃС‚Р°РЅРѕРІСЏРІР°РЅРµ.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b><span lang=\"en-US\">VI.\r\n</span>РџР Р•Р”РЎР РћР§РќРђ Р�Р—Р�РЎРљРЈР•РњРћРЎРў</b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».11. (1) РљР Р•Р”Р�РўРћР РЄРў\r\nРјРѕР¶Рµ РґР° РѕР±СЏРІРё РєСЂРµРґРёС‚Р° Р·Р° РїСЂРµРґСЃСЂРѕС‡РЅРѕ\r\nРёР·РёСЃРєСѓРµРј РїСЂРё РЅР°Р»РёС‡РёРµ РЅР° РЅСЏРєРѕРµ РѕС‚ СЃР»РµРґРЅРёС‚Рµ\r\nСѓСЃР»РѕРІРёСЏ: </font></font>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">1. РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў\r\nРµ РІ Р·Р°Р±Р°РІР° РїРѕ РѕС‚РЅРѕС€РµРЅРёРµ РЅР° РєРѕСЏС‚Рѕ Рё Рё РґР°\r\nРµ РїРѕРіР°СЃРёС‚РµР»РЅР° РІРЅРѕСЃРєР° СЃСЉРіР»Р°СЃРЅРѕ РџРѕРіР°СЃРёС‚РµР»РЅРёСЏ\r\nРїР»Р°РЅ, РџСЂРёР»РѕР¶РµРЅРёРµ в„– 1 РєСЉРј РґРѕРіРѕРІРѕСЂР°; </font></font>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">2. РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў\r\nРµ РґРµРєР»Р°СЂРёСЂР°Р» РЅРµРІРµСЂРЅРё Рё/РёР»Рё РЅРµРїСЉР»РЅРё\r\nРѕР±СЃС‚РѕСЏС‚РµР»СЃС‚РІР° Рё РґР°РЅРЅРё РІ Р�СЃРєР°РЅРµС‚Рѕ Р·Р°\r\nРїРѕС‚СЂРµР±РёС‚РµР»СЃРєРё РєСЂРµРґРёС‚, РёР»Рё РЅРµ Рµ РёРЅС„РѕСЂРјРёСЂР°Р»\r\nСЃРІРѕРµРІСЂРµРјРµРЅРЅРѕ РљР Р•Р”Р�РўРћР Рђ Р·Р° РЅР°СЃС‚СЉРїРёР»Рё\r\nРїСЂРѕРјРµРЅРё РІ РѕР±СЃС‚РѕСЏС‚РµР»СЃС‚РІР°С‚Р° Рё РґР°РЅРЅРёС‚Рµ,\r\nРїРѕСЃРѕС‡РµРЅРё РІ Р�СЃРєР°РЅРµС‚Рѕ Р·Р° РєСЂРµРґРёС‚.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">(2) Р’ СЃР»СѓС‡Р°Р№ С‡Рµ\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў Рµ РІ Р·Р°Р±Р°РІР° РїСЂРё\r\nРїР»Р°С‰Р°РЅРµС‚Рѕ РЅР° РїРѕРІРµС‡Рµ РѕС‚ 2 (РґРІРµ) РїРѕРіР°СЃРёС‚РµР»РЅРё\r\nРІРЅРѕСЃРєРё, СЃСЉРіР»Р°СЃРЅРѕ РџРѕРіР°СЃРёС‚РµР»РЅРёСЏ РїР»Р°РЅ,\r\nРїСЂРµРґСЃС‚Р°РІР»СЏРІР°С‰ РџСЂРёР»РѕР¶РµРЅРёРµ в„–1 РєСЉРј РґРѕРіРѕРІРѕСЂР°,\r\nРЅР°СЃС‚СЉРїРІР° Р°РІС‚РѕРјР°С‚РёС‡РЅР° РїСЂРµРґСЃСЂРѕС‡РЅР°\r\nРёР·РёСЃРєСѓРµРјРѕСЃС‚ РЅР° РІСЃРёС‡РєРё Р·Р°РґСЉР»Р¶РµРЅРёСЏ РЅР°\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇ РїРѕ РґРѕРіРѕРІРѕСЂР°.\r\nР�Р·РёСЃРєСѓРµРјРѕСЃС‚С‚Р° РїРѕ РїСЂРµРґС…РѕРґРЅРѕС‚Рѕ РёР·СЂРµС‡РµРЅРёРµ\r\nРЅР°СЃС‚СЉРїРІР°, Р±РµР· РґР° Рµ РЅРµРѕР±С…РѕРґРёРјРѕ РєР°РєРІРѕС‚Рѕ\r\nРё РґР° Рµ РІРѕР»РµРёР·СЏРІР»РµРЅРёРµ РЅР° СЃС‚СЂР°РЅРёС‚Рµ.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">(3) РџСЂРё РЅР°СЃС‚СЉРїРІР°РЅРµ\r\nРЅР° РїСЂРµРґСЃСЂРѕС‡РЅР° РёР·РёСЃРєСѓРµРјРѕСЃС‚ РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРўС‚\r\nРµ РґР»СЉР¶РµРЅ РЅРµР·Р°Р±Р°РІРЅРѕ РґР° РїРѕРіР°СЃРё РІСЃРёС‡РєРё\r\nСЃРІРѕРё Р·Р°РґСЉР»Р¶РµРЅРёСЏ РїРѕ Р”РѕРіРѕРІРѕСЂР° Р·Р° РєСЂРµРґРёС‚,\r\nРІРµРґРЅРѕ СЃСЉСЃ СЃСЉРѕС‚РІРµС‚РЅР°С‚Р° Р»РёС…РІР° Р·Р° Р·Р°Р±Р°РІР°\r\n</font></font>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b>VII. РџР Р•РҐР’РЄР Р›РЇРќР•\r\nР’Р—Р•РњРђРќР�РЇРўРђ РќРђ РљР Р•Р”Р�РўРћР Рђ</b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».12. РљР Р•Р”Р�РўРћР РЄРў\r\nРёРјР° РїСЂР°РІРѕ РґР° РїСЂРµС…РІСЉСЂР»Рё РІР·РµРјР°РЅРёСЏС‚Р° СЃРё\r\nРїРѕ С‚РѕР·Рё РґРѕРіРѕРІРѕСЂ РЅР° С‚СЂРµС‚Рё Р»РёС†Р°, РєР°С‚Рѕ Рµ\r\nРґР»СЉР¶РµРЅ РґР° СѓРІРµРґРѕРјРё РїРёСЃРјРµРЅРѕ РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇ\r\nР·Р° РёР·РІСЉСЂС€РµРЅРѕС‚Рѕ РїСЂРµС…РІСЉСЂР»СЏРЅРµ.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<br>\r\n</p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><b>IX. Р”Р РЈР“Р�\r\nРЈРЎР›РћР’Р�РЇ</b></font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\">Р§Р».13.\r\nРљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў РґР°РІР° СЃСЉРіР»Р°СЃРёРµ,\r\nРљР Р•Р”Р�РўРћР РЄРў РІ РєР°С‡РµСЃС‚РІРѕС‚Рѕ РјСѓ РЅР° Р°РґРјРёРЅРёСЃС‚СЂР°С‚РѕСЂ\r\nРЅР° Р»РёС‡РЅРё РґР°РЅРЅРё РїРѕ СЃРјРёСЃСЉР»Р° РЅР° Р—Р—Р›Р”, РґР°\r\nРѕР±СЂР°Р±РѕС‚РІР° РїСЂРµРґРѕСЃС‚Р°РІРµРЅРёС‚Рµ РјСѓ Р»РёС‡РЅРё РґР°РЅРЅРё\r\nРїРѕ СЂРµРґР° РЅР° СЂР°Р·РґРµР» <span lang=\"en-US\">III</span> РѕС‚\r\nРћР±С‰РёС‚Рµ СѓСЃР»РѕРІРёСЏ.</font></font></p>\r\n<p class=\"western\" style=\"margin-bottom: 0in; line-height: 115%\" align=\"JUSTIFY\" lang=\"bg-BG\">\r\n<font face=\"Verdana, sans-serif\"><font size=\"2\"><span lang=\"en-US\">Р§Р».1</span>4<span lang=\"en-US\">.\r\nРЎ РїРѕРґРїРёСЃРІР°РЅРµС‚Рѕ РЅР° С‚РѕР·Рё РґРѕРіРѕРІРѕСЂ,\r\n</span>РљР Р•Р”Р�РўРћРџРћР›РЈР§РђРўР•Р›РЇРў<span lang=\"en-US\">\r\nСѓРґРѕСЃС‚РѕРІРµСЂСЏРІР°, С‡Рµ РїСЂРµРґРё РїРѕРґРїРёСЃРІР°РЅРµС‚Рѕ РјСѓ</span>\r\n<span lang=\"en-US\">Рµ </span>РїРѕР»