use crediline;









-- --------------------------------------------------------------------------------
-- Routine DDL
-- Note: comments before and after the routine body will not be stored by the server
-- --------------------------------------------------------------------------------
DELIMITER $$

CREATE DEFINER=`root`@`localhost` FUNCTION `select_first_credit_date`(person_id bigint) RETURNS datetime
BEGIN
	DECLARE credit_creation_date datetime;
	SET credit_creation_date = (select c.creation_date from credits c where c.person_id = person_id order by c.creation_date limit 1);
	IF credit_creation_date is not null THEN
      RETURN credit_creation_date;
    END IF;
RETURN null;
END